import { mapState } from 'vuex';

export default {
	name: 'settings',
	computed: {
		wordWrap: {
			get () {
				return this.$store.state.settings.wordWrap;
			},
			set (value) {
				this.$store.commit('wordWrap', value)
			}
		},
		autoCopy: {
			get () {
				return this.$store.state.settings.autoCopy
			},
			set (value) {
				this.$store.commit('autoCopy', value)
			}
		},
	},
}
